class DirPack {

	var $out = '';

	function __construct ($dir) {
		if (@function_exists('gzcompress')) {
			if (count($dir) > 0) {
				foreach ($dir as $file) {
					if (is_file($file)) {
						$filecode = file_get_contents($file);
						if (is_array($dir)) $file = basename($file);
						$this->fileZip($filecode , $file);
					}
				}
				$this->out = $this->packFile();
			}
			return true;
		} else return false;
	}

	var $datasec      = [];

	var $ctrl_dir     = [];

	var $eof_ctrl_dir = "\x50\x4b\x05\x06\x00\x00\x00\x00";

	var $old_offset   = 0;

	function at ($atunix = 0) {
		$unixarr = ($atunix == 0) ? getdate() : getdate($atunix);
		if ($unixarr['year'] < 1980) {
			$unixarr['year']    = 1980;
			$unixarr['mon']     = 1;
			$unixarr['mday']    = 1;
			$unixarr['hours']   = 0;
			$unixarr['minutes'] = 0;
			$unixarr['seconds'] = 0;
		}
		return (($unixarr['year'] - 1980) << 25) | ($unixarr['mon'] << 21) | ($unixarr['mday'] << 16) |
			($unixarr['hours'] << 11) | ($unixarr['minutes'] << 5) | ($unixarr['seconds'] >> 1);
	}

	function fileZip ($data , $name , $time = 0) {
		$name     = str_replace('\\' , '/' , $name);
		$dtime    = dechex($this->at($time));
		$hexdtime = '\x' . $dtime[6] . $dtime[7]
			. '\x' . $dtime[4] . $dtime[5]
			. '\x' . $dtime[2] . $dtime[3]
			. '\x' . $dtime[0] . $dtime[1];
		eval('$hexdtime = "' . $hexdtime . '";');
		$fr               = "\x50\x4b\x03\x04";
		$fr               .= "\x14\x00";
		$fr               .= "\x00\x00";
		$fr               .= "\x08\x00";
		$fr               .= $hexdtime;
		$unc_len          = strlen($data);
		$crc              = crc32($data);
		$zdata            = gzcompress($data);
		$c_len            = strlen($zdata);
		$zdata            = substr(substr($zdata , 0 , strlen($zdata) - 4) , 2);
		$fr               .= pack('V' , $crc);
		$fr               .= pack('V' , $c_len);
		$fr               .= pack('V' , $unc_len);
		$fr               .= pack('v' , strlen($name));
		$fr               .= pack('v' , 0);
		$fr               .= $name;
		$fr               .= $zdata;
		$fr               .= pack('V' , $crc);
		$fr               .= pack('V' , $c_len);
		$fr               .= pack('V' , $unc_len);
		$this->datasec[]  = $fr;
		$new_offset       = strlen(implode('' , $this->datasec));
		$cdrec            = "\x50\x4b\x01\x02";
		$cdrec            .= "\x00\x00";
		$cdrec            .= "\x14\x00";
		$cdrec            .= "\x00\x00";
		$cdrec            .= "\x08\x00";
		$cdrec            .= $hexdtime;
		$cdrec            .= pack('V' , $crc);
		$cdrec            .= pack('V' , $c_len);
		$cdrec            .= pack('V' , $unc_len);
		$cdrec            .= pack('v' , strlen($name));
		$cdrec            .= pack('v' , 0);
		$cdrec            .= pack('v' , 0);
		$cdrec            .= pack('v' , 0);
		$cdrec            .= pack('v' , 0);
		$cdrec            .= pack('V' , 32);
		$cdrec            .= pack('V' , $this->old_offset);
		$this->old_offset = $new_offset;
		$cdrec            .= $name;
		$this->ctrl_dir[] = $cdrec;
	}

	function packFile () {
		$data    = implode('' , $this->datasec);
		$ctrldir = implode('' , $this->ctrl_dir);
		return $data . $ctrldir . $this->eof_ctrl_dir . pack('v' , sizeof($this->ctrl_dir)) . pack('v' , sizeof($this->ctrl_dir)) . pack('V' , strlen($ctrldir)) . pack('V' , strlen($data)) . "\x00\x00";
	}
}

class PhpZip {

	var $file_count  = 0;

	var $datastr_len = 0;

	var $dirstr_len  = 0;

	var $filedata    = '';

	var $gzfilename;

	var $fp;

	var $dirstr      = '';

	function unix2DosTime ($unixtime = 0) {
		$timearray = ($unixtime == 0) ? getdate() : getdate($unixtime);

		if ($timearray['year'] < 1980) {
			$timearray['year']    = 1980;
			$timearray['mon']     = 1;
			$timearray['mday']    = 1;
			$timearray['hours']   = 0;
			$timearray['minutes'] = 0;
			$timearray['seconds'] = 0;
		}

		return (($timearray['year'] - 1980) << 25) | ($timearray['mon'] << 21) | ($timearray['mday'] << 16) |
			($timearray['hours'] << 11) | ($timearray['minutes'] << 5) | ($timearray['seconds'] >> 1);
	}

	function startFile ($path = "web.zip") {
		$this->gzfilename = $path;
		$mypathdir        = [];
		do {
			$mypathdir[] = $path = dirname($path);
		} while ($path != '.');
		@end($mypathdir);
		do {
			$path = @current($mypathdir);
			@mkdir($path);
		} while (@prev($mypathdir));

		if ($this->fp = @fopen($this->gzfilename , "w")) {
			return true;
		}
		return false;
	}

	function addFile ($data , $name) {
		$name = str_replace('\\' , '/' , $name);

		if (strrchr($name , '/') == '/') return $this->addDir($name);

		$dtime    = dechex($this->unix2DosTime());
		$hexdtime = '\x' . $dtime[6] . $dtime[7]
			. '\x' . $dtime[4] . $dtime[5]
			. '\x' . $dtime[2] . $dtime[3]
			. '\x' . $dtime[0] . $dtime[1];
		eval('$hexdtime = "' . $hexdtime . '";');

		$unc_len = strlen($data);
		$crc     = crc32($data);
		$zdata   = gzcompress($data);
		$c_len   = strlen($zdata);
		$zdata   = substr(substr($zdata , 0 , strlen($zdata) - 4) , 2);

		$datastr = "\x50\x4b\x03\x04";
		$datastr .= "\x14\x00";
		$datastr .= "\x00\x00";
		$datastr .= "\x08\x00";
		$datastr .= $hexdtime;
		$datastr .= pack('V' , $crc);
		$datastr .= pack('V' , $c_len);
		$datastr .= pack('V' , $unc_len);
		$datastr .= pack('v' , strlen($name));
		$datastr .= pack('v' , 0);
		$datastr .= $name;
		$datastr .= $zdata;
		$datastr .= pack('V' , $crc);
		$datastr .= pack('V' , $c_len);
		$datastr .= pack('V' , $unc_len);

		fwrite($this->fp , $datastr);
		$my_datastr_len = strlen($datastr);
		unset($datastr);

		$dirstr = "\x50\x4b\x01\x02";
		$dirstr .= "\x00\x00";
		$dirstr .= "\x14\x00";
		$dirstr .= "\x00\x00";
		$dirstr .= "\x08\x00";
		$dirstr .= $hexdtime;
		$dirstr .= pack('V' , $crc);
		$dirstr .= pack('V' , $c_len);
		$dirstr .= pack('V' , $unc_len);
		$dirstr .= pack('v' , strlen($name));
		$dirstr .= pack('v' , 0);
		$dirstr .= pack('v' , 0);
		$dirstr .= pack('v' , 0);
		$dirstr .= pack('v' , 0);
		$dirstr .= pack('V' , 32);
		$dirstr .= pack('V' , $this->datastr_len);
		$dirstr .= $name;

		$this->dirstr .= $dirstr;

		$this->file_count ++;
		$this->dirstr_len  += strlen($dirstr);
		$this->datastr_len += $my_datastr_len;
	}

	function addDir ($name) {
		$name    = str_replace("\\" , "/" , $name);
		$datastr = "\x50\x4b\x03\x04\x0a\x00\x00\x00\x00\x00\x00\x00\x00\x00";

		$datastr .= pack("V" , 0) . pack("V" , 0) . pack("V" , 0) . pack("v" , strlen($name));
		$datastr .= pack("v" , 0) . $name . pack("V" , 0) . pack("V" , 0) . pack("V" , 0);

		fwrite($this->fp , $datastr);
		$my_datastr_len = strlen($datastr);
		unset($datastr);

		$dirstr = "\x50\x4b\x01\x02\x00\x00\x0a\x00\x00\x00\x00\x00\x00\x00\x00\x00";
		$dirstr .= pack("V" , 0) . pack("V" , 0) . pack("V" , 0) . pack("v" , strlen($name));
		$dirstr .= pack("v" , 0) . pack("v" , 0) . pack("v" , 0) . pack("v" , 0);
		$dirstr .= pack("V" , 16) . pack("V" , $this->datastr_len) . $name;

		$this->dirstr .= $dirstr;

		$this->file_count ++;
		$this->dirstr_len  += strlen($dirstr);
		$this->datastr_len += $my_datastr_len;
	}

	function createFile () {
		$endstr = "\x50\x4b\x05\x06\x00\x00\x00\x00" .
			pack('v' , $this->file_count) .
			pack('v' , $this->file_count) .
			pack('V' , $this->dirstr_len) .
			pack('V' , $this->datastr_len) .
			"\x00\x00";

		fwrite($this->fp , $this->dirstr . $endstr);
		fclose($this->fp);
	}
}

